import React, { useEffect, useState } from "react";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  Tooltip,
  CartesianGrid,
  ReferenceDot,
  Legend,
  ResponsiveContainer
} from "recharts";
import "./App.css";

function App() {
  const [prices, setPrices] = useState([]);
  const [events, setEvents] = useState([]);
  const [startDate, setStartDate] = useState("2000-01-01");
  const [endDate, setEndDate] = useState("2023-12-31");

  useEffect(() => {
    fetch("http://127.0.0.1:5000/api/prices")
      .then(res => res.json())
      .then(data =>
        setPrices(
          data.map(d => ({
            ...d,
            Date: new Date(d.Date).toISOString().slice(0, 10)
          }))
        )
      );

    fetch("http://127.0.0.1:5000/api/events")
      .then(res => res.json())
      .then(data =>
        setEvents(
          data.map(d => ({
            ...d,
            Event_Date: new Date(d.Event_Date).toISOString().slice(0, 10)
          }))
        )
      );
  }, []);

  const filteredPrices = prices.filter(
    p => p.Date >= startDate && p.Date <= endDate
  );

  return (
    <div style={{ padding: "20px" }}>
      <h2>Brent Oil Prices with Key Events</h2>

      {/* Filters */}
      <div style={{ marginBottom: "10px" }}>
        <label>Start Date: </label>
        <input type="date" value={startDate} onChange={e => setStartDate(e.target.value)} />
        <label style={{ marginLeft: "10px" }}>End Date: </label>
        <input type="date" value={endDate} onChange={e => setEndDate(e.target.value)} />
      </div>

      {/* Responsive Chart */}
      <ResponsiveContainer width="100%" height={400}>
        <LineChart data={filteredPrices}>
          <CartesianGrid stroke="#ccc" />
          <XAxis dataKey="Date" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Line type="monotone" dataKey="Price" stroke="#8884d8" />

          {/* Event markers */}
          {events.map((event, idx) => {
            const priceAtEvent = filteredPrices.find(
              p => p.Date === event.Event_Date
            )?.Price;

            if (!priceAtEvent) return null;

            return (
              <ReferenceDot
                key={idx}
                x={event.Event_Date}
                y={priceAtEvent}
                r={5}
                fill="red"
                label={{ position: "top", value: event.Event_Description }}
              />
            );
          })}
        </LineChart>
      </ResponsiveContainer>
    </div>
  );
}

export default App;
