import React, { useEffect, useState } from 'react';
import { LineChart, Line, XAxis, YAxis, Tooltip, CartesianGrid, ReferenceDot, Legend } from 'recharts';
import './App.css';

function App() {
  const [prices, setPrices] = useState([]);
  const [events, setEvents] = useState([]);

  // Fetch Brent prices from backend
  useEffect(() => {
    fetch('http://127.0.0.1:5000/api/prices') // <-- Correct path
      .then(res => res.json())
      .then(data => {
        const formatted = data.map(d => ({
          ...d,
          Date: new Date(d.Date).toLocaleDateString()
        }));
        setPrices(formatted);
      })
      .catch(err => console.error('Error fetching prices:', err));
  }, []);

  // Fetch events from backend
  useEffect(() => {
    fetch('http://127.0.0.1:5000/api/events') // <-- Correct path
      .then(res => res.json())
      .then(data => {
        const formatted = data.map(d => ({
          ...d,
          Event_Date: new Date(d.Event_Date).toLocaleDateString()
        }));
        setEvents(formatted);
      })
      .catch(err => console.error('Error fetching events:', err));
  }, []);

  return (
    <div className="App" style={{ padding: '20px' }}>
      <h2>Brent Oil Prices with Key Events</h2>
      <LineChart width={900} height={450} data={prices}>
        <XAxis dataKey="Date" />
        <YAxis />
        <Tooltip />
        <CartesianGrid stroke="#ccc" />
        <Line type="monotone" dataKey="Price" stroke="#8884d8" />

        {/* Add event markers */}
        {events.map((event, index) => (
          <ReferenceDot
            key={index}
            x={event.Event_Date}
            y={prices.find(p => p.Date === event.Event_Date)?.Price || 0}
            r={5}
            fill="red"
            label={event.Event_Description}
            stroke="none"
          />
        ))}

        <Legend />
      </LineChart>
    </div>
  );
}

export default App;
